import React from "react";

export default class RenderLoader extends React.Component
{
	constructor(props)
	{
		super(props);

		this.state=
		{
			base64EncodedLoaderHTML: ""
		}; //base64EncodedLoaderHTML contains the Base64 encoded HTML code of the loader
	}

	render()
	{
		return <div dangerouslySetInnerHTML={{__html: atob(this.state.base64EncodedLoaderHTML)}}/>;
	}
}
